INSERT INTO ebdb.thread_comments (id, content, user_id, thread_id, is_retrieved, retrieved_at, is_answer, created_at, updated_at, deleted_at) VALUES (59, 'Morning after pills prevent ovulation from occuring, in most cases if ovulation had occurred before the pill is used, then the pill wont have any effect in preventing the pregnancy from occuring.  There are other  contraception methods that will work despite the occurrence of ovulation before their usage e.g Intrauterine devices.', 7, 26, 0, null, 0, '2018-09-02 21:15:13', '2019-07-07 16:21:00', null);
INSERT INTO ebdb.thread_comments (id, content, user_id, thread_id, is_retrieved, retrieved_at, is_answer, created_at, updated_at, deleted_at) VALUES (60, 'Stretch marks usually appear when there is a rapid weight gain, which leads to a stretch of the skin, as against what might happen in an incremental weight gain that allows the skin to adjust. So like you would like to know, a new appearance of it will point towards weight gain, but the best way to know if there is a change in body mass is to measure your weight and compare over time.', 7, 27, 0, null, 0, '2018-09-09 19:37:04', '2019-07-07 16:21:26', null);
