INSERT INTO ebdb.thread_comments (id, content, user_id, thread_id, is_retrieved, retrieved_at, is_answer, created_at, updated_at, deleted_at) VALUES (64, 'I guess you are talking about ruptured uterus and it is a rare event.  The uterus is made up of a mass of muscles, so depending on its state, integrity and rate of contraction, it could "tear" and this will be described as a rupture.  It can also happen in uterus that have been breached before due to ceaserean section.', 7, 30, 0, null, 0, '2018-09-11 23:57:15', '2019-07-07 16:23:08', null);
