INSERT INTO ebdb.thread_comments (id, content, user_id, thread_id, is_retrieved, retrieved_at, is_answer, created_at, updated_at, deleted_at) VALUES (75, 'You also might need to make your house more less sickness inducing, let your children sleep under mosquito nets, ensure hygiene in their food preparation, keep them as clean as possible, ensure they eat well (fruits, vegetables and protein) and also find out how their school is (children pick up diseases from each other in schools at times). If you can maintain these, the reoccurrence of infections and sickness will reduce', 7, 35, 0, null, 0, '2018-10-05 02:13:09', '2019-07-07 16:26:16', null);
