INSERT INTO ebdb.thread_comments (id, content, user_id, thread_id, is_retrieved, retrieved_at, is_answer, created_at, updated_at, deleted_at) VALUES (446, 'A Cycle length of 21-35 days is considered regular, however because of the varying lengths you have, it will be harder to calculate your specific ovulatory window.
