INSERT INTO ebdb.thread_comments (id, content, user_id, thread_id, is_retrieved, retrieved_at, is_answer, created_at, updated_at, deleted_at) VALUES (489, 'Causes of lower abdomen pain are varied. The pain may be negligible or could be a sign of something ominous or quite serious. 
