INSERT INTO ebdb.thread_comments (id, content, user_id, thread_id, is_retrieved, retrieved_at, is_answer, created_at, updated_at, deleted_at) VALUES (588, 'Inflammation of hair follicules can arise from infection by bacteria, fungus, from blockage of the pores or ingrown hairs.

