INSERT INTO ebdb.thread_comments (id, content, user_id, thread_id, is_retrieved, retrieved_at, is_answer, created_at, updated_at, deleted_at) VALUES (705, 'Several conditions can cause white spots on the tonsils. There’s often an associated sore throat, often indicative of an infection. Treatment varies depending on the cause.

