INSERT INTO ebdb.thread_comments (id, content, user_id, thread_id, is_retrieved, retrieved_at, is_answer, created_at, updated_at, deleted_at) VALUES (1036, 'Emergency contraceptive pills (morning-after pills) are a safe and effective way to prevent pregnancy after unprotected sex.

