INSERT INTO ebdb.thread_comments (id, content, user_id, thread_id, is_retrieved, retrieved_at, is_answer, created_at, updated_at, deleted_at) VALUES (2226, 'If your average menstrual cycle is 28 days, you ovulate around day 14, and your most fertile days are days 12, 13 and 14.
